CREATE PROCEDURE [dbo].[SP_UPDATE_BORROWER]
	@BORROWER_ID		nvarchar(50),
	@FIRST_NAME			nvarchar(50),
	@MIDDLE_NAME		nvarchar(50),
	@LAST_NAME			nvarchar(50),
	@DOB				nvarchar(10),
	@AGE				INT,
	@CONTACT_NO			nvarchar(15),
	@ADDRESS			nvarchar(200),
	@MODIFIED_DATE		nvarchar(10),		
	@MODIFIED_BY		nvarchar(20),
	@STATUS 			nvarchar(20),
	@PROFILE_PIC		nvarchar(max)
AS

IF EXISTS(SELECT BORROWER_ID FROM TBL_BORROWER WHERE LTRIM(RTRIM(BORROWER_ID)) = @BORROWER_ID)
BEGIN
	UPDATE TBL_BORROWER 
	SET FIRST_NAME		= @FIRST_NAME,
		MIDDLE_NAME		= @MIDDLE_NAME,
		LAST_NAME		= @LAST_NAME,
		DOB				= @DOB,
		AGE				= @AGE,
		CONTACT_NO		= @CONTACT_NO,
		[ADDRESS]		= @ADDRESS,
		MODIFIED_DATE	= @MODIFIED_DATE,
		MODIFIED_BY		= @MODIFIED_BY,
		[STATUS]		= @STATUS,
		PROFILE_PIC		= @PROFILE_PIC
	WHERE LTRIM(RTRIM(BORROWER_ID)) = @BORROWER_ID
END
