CREATE PROCEDURE [dbo].[SP_RETURN_BOOK]
@BOOK_ID		NVARCHAR(50),
@BORROWER_ID	NVARCHAR(50),
@BOOK_NO		NVARCHAR(50),
@RETURN_DATE	nvarchar(10),
@RETURN_BY		nvarchar(15),
@TOTAL_AMOUNT	DECIMAL(6,2),
@REMARKS		NVARCHAR(20),
@STATUS			NVARCHAR(20)
AS			

DECLARE @TRANSACTION_NO NVARCHAR(15)

IF @TOTAL_AMOUNT <> 0
BEGIN 
	SET @TRANSACTION_NO = (SELECT TRANSACTION_NO FROM TBL_BORROWEDBOOKS WHERE BOOK_ID = @BOOK_ID
	AND BOOK_NO = @BOOK_NO AND BORROWER_ID = @BORROWER_ID AND [STATUS] = 'BORROWED')
		
	INSERT INTO TBL_PENALTIES
	SELECT @TRANSACTION_NO,@RETURN_DATE, @TOTAL_AMOUNT
END

		
IF LTRIM(RTRIM(@REMARKS)) = 'DAMAGE' OR LTRIM(RTRIM(@REMARKS)) = 'LOST'
BEGIN
	UPDATE TBL_BOOKS SET [STATUS] = 'INACTIVE',REMARKS = LTRIM(RTRIM(@REMARKS))
	WHERE ID = @BOOK_ID AND BOOK_NO = @BOOK_NO
END

UPDATE TBL_BORROWEDBOOKS			SET	[STATUS]	= @STATUS,
							RETURN_DATE     = @RETURN_DATE,
							RETURN_BY       = @RETURN_BY								
						WHERE 	BORROWER_ID = @BORROWER_ID	AND BOOK_ID	= @BOOK_ID
						AND BOOK_NO = @BOOK_NO  	
											 
GO
