CREATE PROCEDURE [dbo].[SP_GET_BOOK_AVAILABILITY]
@BOOK_ID		NVARCHAR(50)
AS

DECLARE @TBL_BOOK_ISBN TABLE
(
BOOK_ID			NVARCHAR(15),
BOOK_NO			NVARCHAR(15),
ISBN_NUMBER		NVARCHAR(20),
REMARKS			NVARCHAR(20),
[STATUS]		NVARCHAR(15),
RECORD_COUNT	INT IDENTITY(1,1)
)

DECLARE @REMARKS		NVARCHAR(20),
		@STATUS			NVARCHAR(15),		
		@BOOK_NO		NVARCHAR(15),
		@MIN			INT,
		@MAX			INT
		
		

INSERT INTO @TBL_BOOK_ISBN
SELECT ID,BOOK_NO,ISBN_NUMBER,REMARKS,[STATUS] FROM TBL_BOOKS WHERE ID = @BOOK_ID

SELECT	@MIN =  MIN(RECORD_COUNT),
		@MAX =  MAX(RECORD_COUNT) FROM @TBL_BOOK_ISBN
		
		
WHILE @MIN <= @MAX
BEGIN
	SET @BOOK_NO = (SELECT BOOK_NO FROM @TBL_BOOK_ISBN WHERE RECORD_COUNT = @MIN)
		
	SET @REMARKS = (SELECT [STATUS] FROM TBL_BOOKS WHERE [STATUS] = 'INACTIVE' AND LTRIM(RTRIM(BOOK_NO)) = LTRIM(RTRIM(@BOOK_NO)))
	IF @REMARKS IS NOT NULL GOTO JUMP_HERE
	SET @REMARKS = (SELECT [STATUS] FROM TBL_BORROWEDBOOKS WHERE [STATUS] = 'BORROWED' AND LTRIM(RTRIM(BOOK_NO)) = LTRIM(RTRIM(@BOOK_NO)))
	IF @REMARKS IS NOT NULL GOTO JUMP_HERE
	SET @REMARKS = (SELECT [STATUS] FROM TBL_BORROWERREQUEST WHERE [STATUS] = 'REQUEST' AND LTRIM(RTRIM(BOOK_NO)) = LTRIM(RTRIM(@BOOK_NO)))
	IF @REMARKS IS NOT NULL GOTO JUMP_HERE	
	
	IF @REMARKS IS NULL
	BEGIN
		SET @REMARKS = 'ACTIVE'
	END		
	
	JUMP_HERE:				
	UPDATE @TBL_BOOK_ISBN SET [STATUS] = @REMARKS WHERE BOOK_NO = @BOOK_NO	
	
	
	SET @MIN = @MIN + 1
END

SELECT * FROM @TBL_BOOK_ISBN
GO
